,p_attribute_01=>'SQL_STATEMENT'
,p_attribute_03=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT ',
'    G.SGST,',
'    G.CGST,',
'    P.DISCOUNT,',
'    (P.PRODUCT_COST * :P16_PRODUCT_QTY),',
'    (',
'        (P.PRODUCT_COST * :P16_PRODUCT_QTY) + ',
'        (((P.PRODUCT_COST * :P16_PRODUCT_QTY) * G.SGST)/100) + ',
'        (((P.PRODUCT_COST * :P16_PRODUCT_QTY) * G.CGST)/100) - ',
'        (((P.PRODUCT_COST * :P16_PRODUCT_QTY) * P.DISCOUNT)/100)',
'    ) AS TotalCostAfterDiscount',
'FROM ',
'    PRODUCT_DETAIL PD',
'JOIN ',
'    GST_DETAIL G ON PD.PRODUCT_CATEGORYID = G.CATEGORY_ID',
'JOIN ',
'    PRODUCT_COST P ON PD.PRODUCT_ID = P.PRODUCT_ID',
'WHERE ',
'    P.PRODUCT_ID = :P16_PRODUCT_ID;',
''))
,p_attribute_07=>'P16_PRODUCT_ID,P16_PRODUCT_QTY,P16_NET_AMOUNT'
,p_attribute_08=>'Y'
,p_attribute_09=>'N'
,p_wait_for_result=>'Y'
);
